	@Test
	public void toJson() {
		
		Map<String, String> map = new HashMap<>();
		map.put("name", "lili");
		map.put("age", "12");
		
		Gson gson = new Gson();
		String result = gson.toJson(map);
		
		System.out.println("toJson: " + result);
	}
	
	@Test
	public void fromJson() {
		
		String test = "{\"name\":\"lili\",\"age\":\"12\"}";
		Gson gson = new Gson();
		@SuppressWarnings("rawtypes")
		Map map = gson.fromJson(test, new TypeToken<Map>(){}.getType());
		System.out.println("from Json: " + map);
	}
	
